            N   = order;   % Order
            Fc1 = cutoffFreq1;  % First Cutoff Frequency
            Fc2 = cutoffFreq2;  % Second Cutoff Frequency

            % Construct an FDESIGN object and call its BUTTER method.
            h  = fdesign.bandpass('N,F3dB1,F3dB2', N, Fc1, Fc2, Fs);
            Hd = design(h, 'butter');
        end
        
